import React, { useEffect,useState } from 'react'
import Game from '../Game'
import { GamingList } from '../../utils/data'
import axios from "axios"





const GamesList = (props) => {

const [data, setData] = useState([])

  useEffect(() => {
    axios.get("https://api.rawg.io/api/platforms?key=d617ab52a89d4357a187a7e170ac681b").then((res)=>setData(res.data))
  })


    return (
      <div id="list">
        {data.map((game, index) =>(
          <Game key={index} game={game} />
        ))}
          </div>
    )

}

export default GamesList
