import react from "react";
import { Slide } from "react-slideshow-image";

const spanStyle = {
  padding: "20px",
  background: "#efefef",
  color: "#000000",
};

const divStyle = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  backgroundSize: "cover",
  height: "400px",
};

const slideImages = [
  {
    url: "https://cdn.pixabay.com/photo/2021/09/07/07/11/game-console-6603120__340.jpg",
    caption: "Slide 1",
  },
  {
    url: "https://cdn.pixabay.com/photo/2021/02/16/18/55/gamer-6022003__340.png",
    caption: "Slide 2",
  },
  {
    url: "https://cdn.pixabay.com/photo/2015/12/23/22/36/minecraft-1106252__340.jpg",
    caption: "Slide 3",
  },
];

const Slideshow = () => {
  return (
    <div className="containerSlide">
      <Slide>
        {slideImages.map((slideImage, index) => (
          <div key={index}>
            <div
              style={{ ...divStyle, backgroundImage: `url(${slideImage.url})` }}
            >
              <span style={spanStyle}>{slideImage.caption}</span>
            </div>
          </div>
        ))}
      </Slide>
    </div>
  );
};

export default Slideshow;
