import React, { useEffect, useState } from 'react';
import Game from '../Game';
import axios from 'axios';

const GamesList = (props) => {
  const { searchValue } = props;

  const [data, setData] = useState([]);
  const [couille, setCouille] = useState(1);

  useEffect(() => {
    axios
      .get(
        'https://api.rawg.io/api/games?key=5e731b63837f49759cde8b1cb3505d80&page_size=5&page=1'
      )
      .then((res) => {
        setData(res.data.results);
        console.log(res.data);
      });
  }, [couille]);
  console.log(data.length);

  return (
    <div id='list'>
      <button onClick={() => setCouille(3)}>coucou </button>
      {data
        .filter((gameChoice) =>
          gameChoice.name.toLowerCase().includes(searchValue)
        )
        .map((game, index) => (
          <Game key={index} game={game} />
        ))}
    </div>
  );
};

export default GamesList;
