import React from 'react'
import Game from '../Game'

const games = [
    {
      name: "Prince of Persia",
      mode: "Solo",
      genre: "Adventure",
    },
    {
      name: "Call of Duty",
      mode: "Solo",
      genre: "FPS",
    },
    {
      name: "Super Smash Bros Melee",
      mode: "Multi",
      genre: "Platform",
    },
    {
      name: "It Takes Two",
      mode: "Coop",
      genre: "Adventure",
    },
    {
        name: "Limbo",
        mode: "Coop",
    genre: "Adventure"}
  ];

const GamesList = (props) => {
    const {searchValue}=props
    return (
        <div id="list">
            {games.filter((game) => game.mode.includes(searchValue)).map((game) => (<Game name={game.name} genre={game.genre} mode={game.mode} />))}
        </div>
    )

}

export default GamesList
